var Handlebars = require('./lib/handlebars');
var context = [{ name: "John Doe" }, { name: "Jane Doe" }];

function output(name, template) {
  console.log(name + '\n======');
  console.log(Handlebars.precompile(template));
  console.log('\n\n\n');
}

output('String', 'foo');

output('Var', '{{foo}}');

output('Block', '{{#.}}{{name}}{{/.}}{{#.}}{{name}}{{/.}}');

output('Hash', '{{foo 1234 baz foo=bar bat=1}}');

output('Inverse', '{{#foo}}{{^}}bar{{/foo}}');

output('Partial', '{{>foo}}');
